'use strict';

var commandEventFilters = [];

module.exports = function(nativeCommandManager) {
  
  nativeCommandManager._assignJSCommandEventFilter((event) => {
    try {
      return commandEventFilters.some(fn => fn(event))
    } catch (err) {
      console.error(err);
      return false;
    }
  });
    
  return {
    add: nativeCommandManager.add,
    remove: nativeCommandManager.remove,
    addCommandEventFilter: (fn) => commandEventFilters.push(fn),
    removeCommandEventFilter: (fn) => {
      commandEventFilters = commandEventFilters.filter(e => e !== fn);
    }
  };
};