'use strict';

// used only by jsdoc

/**
 * keymap.ymlで使用される条件を扱うオブジェクト。
 * @namespace
 * @memberof module:silkedit
 */
const Condition = {
  /**
   * @readonly
   * @enum {number}
   */
  Operator: {
    EQUALS,
    NOT_EQUALS
  },
  /**
   * 条件を追加する。
   * @function
   * @param {string} key - 条件キー
   * @param {object} cond - keyValue()かisSatisfied(operator, operand)メソッドを持つオブジェクト
   * @param {function} cond.keyValue - stringを返す関数
   * @param {function} cond.isSatisfied - (operator, operand)を引数にとってbooleanを返す関数
   */
  add: (key, cond) => {},
  /**
   * 条件を削除する。
   * @function
   * @param {string} key - 条件キー
   */
  remove: (key) => {},
  /**
   * 条件をチェックする。
   * @function
   * @param {string} keyValue - 条件キーの値
   * @param {module:silkedit.Condition.Operator} operator - 条件となるオペレーター
   * @param {string} operand - 比較する値
   */
  check: (keyValue, operator, operand) => {}
};
 
module.exports = Condition;