'use strict';

const bridge = process.binding('silkeditbridge');

// used only by jsdoc

/**
 * 定数を持つオブジェクト。
 * @namespace
 * @memberof module:silkedit
 */
const Constants = {
  /**
   * パッケージを管理するpackage.jsonへのパス。
   * @returns {string} Macでは```$HOME/.silk/packages/package.json```, Windowsでは```C:\Users\<ユーザ名>\.silk\packages\package.json```
   */
  userRootPackageJsonPath: () => bridge.Constants.userRootPackageJsonPath,
  /**
   * パッケージがインストールされているnode_modulesディレクトリへのパス。
   * @returns {string} Macでは```$HOME/.silk/packages/node_modules```, Windowsでは```C:\Users\<ユーザ名>\.silk\packages\node_modules```
   */
  userPackagesNodeModulesPath: () => bridge.Constants.userPackagesNodeModulesPath,
  /**
   * SilkEditのユーザ設定ファイル等が保存されているルートディレクトリへのパス。
   * @returns {string} Macでは```$HOME/.silk```, Windowsでは```C:\Users\<ユーザ名>\.silk```
   */
  silkHomePath: () => bridge.Constants.silkHomePath
};
 
module.exports = Constants;