'use strict';

var keyEventFilters = [];

module.exports = function(nativeKeymapManager) {
  
  nativeKeymapManager._assignJSKeyEventFilter((event) => {
    try {
      return keyEventFilters.some(fn => fn(event))
    } catch (err) {
      console.error(err);
      return false;
    }
  });
    
  return {
    dispatch: nativeKeymapManager.dispatch,
    load: nativeKeymapManager.load,
    addKeyEventFilter: (fn) => keyEventFilters.push(fn),
    removeKeyEventFilter: (fn) => {
      keyEventFilters = keyEventFilters.filter(e => e !== fn);
    }
  };
};